--
-- Bẫy `categories`
--
DELIMITER $$
CREATE TRIGGER `after_delete_category` AFTER DELETE ON `categories` FOR EACH ROW BEGIN
    DECLARE done INT DEFAULT FALSE;
    DECLARE PostId INT;
	DECLARE cur CURSOR FOR SELECT posts.PostId FROM posts
    WHERE NOT EXISTS (
        SELECT 1
        FROM post_category
        WHERE posts.PostId = post_category.PostId    
    );
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    OPEN cur;
	read_loop: LOOP
        -- Đọc dữ liệu từ con trỏ
        FETCH cur INTO PostId;

        -- Kiểm tra xem đã kết thúc dòng hay chưa
        IF done THEN
            LEAVE read_loop;
        END IF;

        -- Xử lý dữ liệu tại đây
        INSERT INTO post_category (PostId, CategoryId)
		VALUES (PostId, 0);
    END LOOP;

    CLOSE cur;
END
$$
DELIMITER ;

