--
-- Cấu trúc bảng cho bảng `posts`
--

CREATE TABLE `posts` (
  `PostId` int(11) NOT NULL COMMENT 'Khóa chính mã bài đăng',
  `UserId` int(11) NOT NULL COMMENT 'Khóa ngoại mã tài khoản',
  `PostTitle` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Tiêu đề bài đăng',
  `PostDescription` text NOT NULL COMMENT 'Mô tả bài đăng',
  `PostThumbnail` varchar(255) NOT NULL COMMENT 'Ảnh bài đăng',
  `PostContent` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Nội dung bài đăng',
  `PostDate` datetime NOT NULL DEFAULT current_timestamp() COMMENT 'Thời gian bài đăng',
  `PostView` int(11) NOT NULL DEFAULT 0 COMMENT 'Lượt xem bài đăng'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `posts`
--

INSERT INTO `posts` (`PostId`, `UserId`, `PostTitle`, `PostDescription`, `PostThumbnail`, `PostContent`, `PostDate`, `PostView`) VALUES
(97, 1, 'Vui chơi “hết nấc” tại công viên Yomiuriland, Nhật Bản', 'Công viên Yomiuriland được thiết kế theo mô hình giải trí, vui chơi đa dạng, là địa điểm tuyệt vời cho du khách nước ngoài khi đến Tokyo, Nhật Bản.', 'cong-vien-yomiuriland-ivivu3-1536x1152.jpg', '<p><strong style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:inherit;margin:0px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\">Công viên Yomiuriland</strong><span style=\"color:rgb(17,17,17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-size:14px;\"><span style=\"-webkit-text-stroke-width:0px;display:inline !important;float:none;font-style:normal;font-variant-caps:normal;font-variant-ligatures:normal;font-weight:400;letter-spacing:normal;orphans:2;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;\"> được thiết kế theo mô hình giải trí, địa điểm vui chơi, tọa lạc ở Tokyo, Nhật Bản. Mở cửa từ những năm 1964 và được xây dựng trên một khu sườn đồi. Nơi đây có rất nhiều khu vui chơi giải trí thích hợp cho các gia đình và cả cặp đôi.</span></span></p><h4 style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:1.14286rem;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:1.71429rem;margin:0px 0px 10px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-rendering:optimizelegibility;text-transform:uppercase;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\"><strong>CÁC HOẠT ĐỘNG, VUI CHƠI TẠI CÔNG VIÊN</strong></h4><h4 style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:1.14286rem;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:1.71429rem;margin:0px 0px 10px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-rendering:optimizelegibility;text-transform:uppercase;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\"><strong style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-style:inherit;font-variant:inherit;font-variation-settings:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\">THÁP RƠI TỰ DO CRAZY HYUUU VÀ CRAZY TOON</strong></h4><p style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0px 0px 15px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\">Tháp rơi tự do là một trong những trò chơi mạo hiểm, thú vị tại công viên. Bạn sẽ có dịp ngắm nhìn quang cảnh Tokyo ở trên cao. Đây sẽ là trò chơi thử thách lòng can đảm của rất nhiều người chơi khi đến đây.</p><div class=\"wp-caption aligncenter\" style=\"-webkit-text-stroke-width:0px;border-width:0px;clear:both;color:rgb(17, 17, 17);display:block;font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0.4em auto 1.25em;max-width:96%;orphans:2;padding:0px;text-align:center;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;width:1034px;word-spacing:0px;\" id=\"attachment_449810\"><figure class=\"image\" style=\"height:auto;\"><img class=\"wp-image-449810 size-full\" style=\"aspect-ratio:1024/680;border-width:0px;display:block;font:inherit;margin:0px auto;max-width:100%;padding:0px;vertical-align:baseline;\" src=\"https://cdn3.ivivu.com/2024/01/yomiuriland1.png\" alt=\"Công viên Yomiuriland\" srcset=\"https://cdn3.ivivu.com/2024/01/yomiuriland1.png 1024w, https://cdn3.ivivu.com/2024/01/yomiuriland1-459x305.png 459w, https://cdn3.ivivu.com/2024/01/yomiuriland1-768x510.png 768w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" width=\"1024\" height=\"680\" decoding=\"async\" aria-describedby=\"caption-attachment-449810\"></figure><p class=\"wp-caption-text\" style=\"border-width:0px;color:rgb(102, 102, 102);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:0.92857rem;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:1.39286rem;margin:10px 0px 1.25em;padding:0px;vertical-align:baseline;\" id=\"caption-attachment-449810\"><i>Trải nghiệm cảm giác mạnh với Crazy Hyuuu và Crazy Toon</i></p></div><h4 style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:1.14286rem;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:1.71429rem;margin:0px 0px 10px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-rendering:optimizelegibility;text-transform:uppercase;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\"><strong style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-style:inherit;font-variant:inherit;font-variation-settings:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\">TÀU LƯỢN SIÊU TỐC</strong></h4><div class=\"wp-caption aligncenter\" style=\"-webkit-text-stroke-width:0px;border-width:0px;clear:both;color:rgb(17, 17, 17);display:block;font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0.4em auto 1.25em;max-width:96%;orphans:2;padding:0px;text-align:center;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;width:1930px;word-spacing:0px;\" id=\"attachment_449808\"><figure class=\"image\" style=\"height:auto;\"><img class=\"wp-image-449808 size-full\" style=\"aspect-ratio:1920/1080;border-width:0px;display:block;font:inherit;margin:0px auto;max-width:100%;padding:0px;vertical-align:baseline;\" src=\"https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1.jpg\" alt=\"Công viên Yomiuriland\" srcset=\"https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1.jpg 1920w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-540x305.jpg 540w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-1024x576.jpg 1024w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-250x142.jpg 250w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-768x432.jpg 768w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-1536x864.jpg 1536w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-750x422.jpg 750w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-225x127.jpg 225w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-370x208.jpg 370w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-50x28.jpg 50w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-107x60.jpg 107w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-195x110.jpg 195w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-220x125.jpg 220w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-400x225.jpg 400w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu1-140x80.jpg 140w\" sizes=\"(max-width: 1920px) 100vw, 1920px\" width=\"1920\" height=\"1080\" decoding=\"async\" aria-describedby=\"caption-attachment-449808\"></figure><p class=\"wp-caption-text\" style=\"border-width:0px;color:rgb(102, 102, 102);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:0.92857rem;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:1.39286rem;margin:10px 0px 1.25em;padding:0px;vertical-align:baseline;\" id=\"caption-attachment-449808\"><i>Tàu lượn siêu tốc “Bandit”</i></p></div><p style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0px 0px 15px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\">Một trò chơi trải nghiệm cảm giác mạnh tiếp theo là tàu lượn siêu tốc. Đây là tàu lượn siêu tốc nổi tiếng nhất của <em style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\"><i><strong style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-style:inherit;font-variant:inherit;font-variation-settings:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\">công viên Yomiuriland</strong></i></em> mang tên “Bandit”. Với tốc độ tối đa lên tới 110km/h, chạy xuyên qua một khu rừng.</p><h4 style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:1.14286rem;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:1.71429rem;margin:0px 0px 10px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-rendering:optimizelegibility;text-transform:uppercase;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\"><strong style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-style:inherit;font-variant:inherit;font-variation-settings:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\">KHU VUI CHƠI GOODJOB CHO TRẺ EM</strong></h4><div class=\"wp-caption aligncenter\" style=\"-webkit-text-stroke-width:0px;border-width:0px;clear:both;color:rgb(17, 17, 17);display:block;font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0.4em auto 1.25em;max-width:96%;orphans:2;padding:0px;text-align:center;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;width:1034px;word-spacing:0px;\" id=\"attachment_449811\"><figure class=\"image\" style=\"height:auto;\"><img class=\"wp-image-449811 size-full\" style=\"aspect-ratio:1024/576;border-width:0px;display:block;font:inherit;margin:0px auto;max-width:100%;padding:0px;vertical-align:baseline;\" src=\"https://cdn3.ivivu.com/2024/01/yomiuriland2.png\" alt=\"Công viên Yomiuriland\" srcset=\"https://cdn3.ivivu.com/2024/01/yomiuriland2.png 1024w, https://cdn3.ivivu.com/2024/01/yomiuriland2-540x305.png 540w, https://cdn3.ivivu.com/2024/01/yomiuriland2-250x142.png 250w, https://cdn3.ivivu.com/2024/01/yomiuriland2-768x432.png 768w, https://cdn3.ivivu.com/2024/01/yomiuriland2-750x422.png 750w, https://cdn3.ivivu.com/2024/01/yomiuriland2-225x127.png 225w, https://cdn3.ivivu.com/2024/01/yomiuriland2-370x208.png 370w, https://cdn3.ivivu.com/2024/01/yomiuriland2-50x28.png 50w, https://cdn3.ivivu.com/2024/01/yomiuriland2-107x60.png 107w, https://cdn3.ivivu.com/2024/01/yomiuriland2-195x110.png 195w, https://cdn3.ivivu.com/2024/01/yomiuriland2-220x125.png 220w, https://cdn3.ivivu.com/2024/01/yomiuriland2-400x225.png 400w, https://cdn3.ivivu.com/2024/01/yomiuriland2-140x80.png 140w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" width=\"1024\" height=\"576\" loading=\"lazy\" decoding=\"async\" aria-describedby=\"caption-attachment-449811\"></figure><p class=\"wp-caption-text\" style=\"border-width:0px;color:rgb(102, 102, 102);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:0.92857rem;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:1.39286rem;margin:10px 0px 1.25em;padding:0px;vertical-align:baseline;\" id=\"caption-attachment-449811\"><i>Địa điểm vui chơi này được nhiều trẻ em yêu thích</i></p></div><p style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0px 0px 15px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\">Goodjob là một địa điểm vui chơi Tokyo mới được xây dựng gần đây. Đến với khu vui chơi này, trẻ em có thể thỏa thích khám phá và trải nghiệm. Các ngành nghề thủ công Monozukuri với những sản phẩm trưng bày thật với kích cỡ lớn.</p><h4 style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:1.14286rem;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;letter-spacing:normal;line-height:1.71429rem;margin:0px 0px 10px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-rendering:optimizelegibility;text-transform:uppercase;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\"><strong style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-style:inherit;font-variant:inherit;font-variation-settings:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\">NHẢY BUNGEE</strong></h4><p style=\"-webkit-text-stroke-width:0px;border-width:0px;color:rgb(17, 17, 17);font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0px 0px 15px;orphans:2;padding:0px;text-align:justify;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;word-spacing:0px;\">Nếu bạn muốn trải nghiệm một cảm giác mạnh khác tại <em style=\"border-width:0px;font-family:inherit;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:inherit;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:inherit;margin:0px;padding:0px;vertical-align:baseline;\"><i>công viên Yomiuriland, </i></em>hãy thử nhảy Bungee. Nơi bạn phải vượt qua được sự sợ hãi của mình và nhảy xuống từ độ cao rất cao. Độ cao ấy tương đương đứng trên tầng 7 của tòa nhà, cách mặt đất 22 mét.</p><div class=\"wp-caption alignnone\" style=\"-webkit-text-stroke-width:0px;border-width:0px;clear:both;color:rgb(17, 17, 17);display:block;font-family:&quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, Calibri, &quot;Droid Sans&quot;, Helvetica, Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:14px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:0.4em auto 1.25em;max-width:96%;orphans:2;padding:0px;text-align:center;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;vertical-align:baseline;white-space:normal;widows:2;width:1810px;word-spacing:0px;\" id=\"attachment_450134\"><figure class=\"image\" style=\"height:auto;\"><img class=\"wp-image-450134 size-full\" style=\"aspect-ratio:1800/1200;border-width:0px;display:block;font:inherit;margin:0px auto;max-width:100%;padding:0px;vertical-align:baseline;\" src=\"https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1.jpg\" alt=\"Công viên Yomiuriland\" srcset=\"https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1.jpg 1800w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1-458x305.jpg 458w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1-1024x683.jpg 1024w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1-768x512.jpg 768w, https://cdn3.ivivu.com/2024/01/cong-vien-yomiuriland-ivivu-1-1536x1024.jpg 1536w\" sizes=\"(max-width: 1800px) 100vw, 1800px\" width=\"1800\" height=\"1200\" loading=\"lazy\" decoding=\"async\" aria-describedby=\"caption-attachment-450134\"></figure><p class=\"wp-caption-text\" style=\"border-width:0px;color:rgb(102, 102, 102);font-family:&quot;Roboto Slab&quot;, Palatino, Cambria, &quot;Droid Serif&quot;, Georgia, &quot;Times New Roman&quot;, Times, serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size:0.92857rem;font-stretch:inherit;font-variant:inherit;font-variation-settings:inherit;font-weight:inherit;line-height:1.39286rem;margin:10px 0px 1.25em;padding:0px;